from textblob import  TextBlob# Sample Textstexts_for_testing = [ "The movie was amazing!",    "Just came back from a hike.",    "Making pan cakes for breakfast.",    "Traffic is so bad today.",    "Had a great workout this morning"    ]for texts in texts_for_testing:     blob = TextBlob(texts)             # we feeded the blob obj our text already     correct_text = blob.correct()      # no need to feed it text now as we alreaady did     print(f"Original text: {texts}")     print(f"Corrected test: {correct_text}")